INSERT INTO dou_page VALUES('1','about','0','公司简介','<span style=\"color:#505050;font-family:Verdana,;\">\r\n<p class=\"MsoNormal\">\r\n	石家庄鑫宏诺化工有限公司成立于<span>2010</span>年<span>4</span>月，地址位于河北省省会石家庄。主要经营化工产品的批发，销售工业纯碱，食品级纯碱，小苏打，硝酸钠，亚硝酸钠，片碱，尿素等化工产品。<span></span>\r\n</p>\r\n我们尊崇<span>“</span>踏实、拼搏、责任<span>”</span>的企业精神，并以诚信、共赢、开创经营理念，创造良好的办公环境，以全新的管理模式，完善的技术，周到的服务，卓越的品质为生存根本，我们始终坚持用户至上\r\n用心服务于客户，坚持用自己的服务去打动客户。欢迎各位来参观指导工作，如果您对我们的产品感兴趣或者有任何的疑问，您可以直接给我们留言或直接与我们联络，我们将在收到您的信息后，会第一时间及时与您联络。</span>','公司简介','石家庄鑫宏诺化工有限公司成立于2010年4月，地址位于河北省省会石家庄。主要经营化工产品的批发，销售工业纯碱，食品级纯碱，小苏打，硝酸钠，亚硝酸钠，片碱，尿素等化工产品。');
INSERT INTO dou_page VALUES('4','contact','1','联系我们','通讯地址：<br />\r\n<span style=\"color:#D7D7D7;\">--------------------------------------------------------------------------------------------------------------------------------</span><br />\r\n河北省石家庄市长安区谈南路<span>63</span>号睿和中心<span>1909</span>室<br />\r\n<br />\r\n客服邮箱：<br />\r\n<span style=\"color:#D7D7D7;\">--------------------------------------------------------------------------------------------------------------------------------</span><br />\r\n邮箱：<span>sjzxhnhg@163.com</span><br />\r\n<br />\r\n客服电话：<br />\r\n<span style=\"color:#D7D7D7;\">--------------------------------------------------------------------------------------------------------------------------------</span><br />\r\n咨询电话为 0311-68095955 &nbsp;0311-89280989。<br />\r\n客服电话工作时间为周一至周日 8:00-18:00，节假日不休息。<br />\r\n我们将随时为您献上真诚的服务。<br />\r\n<br />\r\n网站网址：<br />\r\n<span style=\"color:#D7D7D7;\">--------------------------------------------------------------------------------------------------------------------------------</span><br />\r\nwww.xxx.com/<br />','联系我们','联系我们');
